import React, {Component} from "react"
import ProductTable from "./ProductTable";
import {ACCESS_LEVEL_ADMIN} from "../config/global_constants";
import {Link} from "react-router-dom";


export default class Sizes extends Component{
    constructor(props) {
        super(props);
        // const coloursAvailable = this.generateColoursAvailable(props.products)
        this.state = {
            products: [],
            isChecked: {
                colours: {
                    red: "Red",
                    orange: "Orange",
                    yellow: "Yellow",
                    green: "Green",
                    blue: "Blue",
                    purple: "Purple",
                    pink: "Pink",
                    black: "Black",
                    white: "White",
                    grey: "Grey",
                    brown: "Brown",
                    multiple: "Multiple"
                }
            }
        }
    }
    handleColourCheckBoxChange = (attribute) => {
        this.setState((prevState) => ({
            isChecked: {
                ...prevState.isChecked,
                colours: {
                    ...prevState.isChecked.colours,
                    [attribute]: !prevState.isChecked.colours[attribute]
                }
            }
        }))
    }

    applyFilters = () => {
        let filteredProducts = [...this.state.products]

        const colourFilters = this.state.isChecked.colours;

        for (const colour in colourFilters) {
            if (colourFilters[colour] === true) {
                filteredProducts = filteredProducts.filter((product) =>
                    product.colour === colour
                )
            }
        }

        console.log(filteredProducts)
        return filteredProducts
    }

    render() {
        let filteredProducts = this.applyFilters();
        return(
            <div className="sizeFilter1">
                <div className="selection">
                    <input
                        id={"Red"}
                        type="checkbox"
                        checked={this.state.isChecked.colours.red}
                        onChange={() => this.handleColourCheckBoxChange()}
                    />
                    <label for="xlarge">Red</label>
                </div>

                <div className="table-container">
                    <ProductTable products={filteredProducts} />
                    {localStorage.accessLevel >= ACCESS_LEVEL_ADMIN ? (
                        <div className="add-new-products">
                            <Link className="blue-button" to={"/AddProduct"}>
                                Add New Product
                            </Link>
                        </div>
                    ) : null}
                </div>

                {/*<div className="selection">*/}
                {/*    <input type="radio" name="size" id="large" value="LARGE"></input>*/}
                {/*    <label for="large">Large</label>*/}
                {/*</div>*/}

                {/*<div className="selection">*/}
                {/*    <input type="radio" name="size" id="medium" value="MEDIUM"></input>*/}
                {/*    <label for="medium">Medium</label>*/}
                {/*</div>*/}

                {/*<div className="selection">*/}
                {/*    <input type="radio" name="size" id="small" value="SMALL"></input>*/}
                {/*    <label for="small">Small</label>*/}
                {/*</div>*/}

                {/*<div className="selection">*/}
                {/*    <input type="radio" name="size" id="xsmall" value="XSMALL"></input>*/}
                {/*    <label for="xsmall">X Small</label>*/}
                {/*</div>*/}
            </div>
        );
    }
}
