import React, {Component} from "react"
import {Link} from "react-router-dom"
import {ACCESS_LEVEL_NORMAL_USER, SERVER_HOST} from "../config/global_constants";
import axios from "axios";



export default class UserProfiles extends Component
{

    componentDidMount() {
        this.props.user.photos.map(photo =>
        {
            return axios.get(`${SERVER_HOST}/user/photo/${photo.filename}`)
                .then(res =>
                {
                    if(res.data)
                    {
                        if (res.data.errorMessage)
                        {
                            console.log(res.data.errorMessage)
                        }
                        else
                        {
                            document.getElementById(photo._id).src = `data:;base64,${res.data.image}`
                        }
                    }
                    else
                    {
                        console.log("Record not found")
                    }
                })
        })
    }

    render()
    {
        return (
            <div className="product-container">
                <div className="products">
                    {this.props.product.photos.map(photo => <img key={photo._id} id={photo._id} alt=""/>)}
                </div>
                {/*<div className="products">*/}
                {/*    <div className="product-info">*/}
                        <div className="info">{this.props.user.name}</div>
                        <div className="info">{this.props.user.email}</div>
                        <div className="product-actions">
                            {localStorage.accessLevel > ACCESS_LEVEL_NORMAL_USER ? <Link className="red-button" to={"/DeleteProduct/" + this.props.product._id}>Delete</Link> : null}
                        </div>
                    {/*</div>*/}
                {/*</div>*/}
            </div>
        )
    }
}
