import React, { Component } from "react"
import { Link } from "react-router-dom"
import axios from "axios"
import CustomerProfileDisplay from "./CustomerProfileDisplay"
import { ACCESS_LEVEL_GUEST, ACCESS_LEVEL_ADMIN, SERVER_HOST } from "../config/global_constants"
import Sidebar from "./Sidebar";
import Navbar from "./Navbar";

export default class DisplayAllCustomers extends Component {
    constructor(props) {
        super(props);
        this.state = {
            users: []
        }
    }

    componentDidMount() {
        axios.get(`${SERVER_HOST}/users`).then((res) => {
            if (res.data) {
                if (res.data.errorMessage) {
                    console.log(res.data.errorMessage)
                } else {
                    console.log("Users read")
                    this.setState({ users: res.data })
                }

            } else {
                console.log("Users not found")
            }
        })
    }
    render() {

        return (
            <div>
                <Navbar/>
                <Sidebar/>
                <CustomerProfileDisplay users={this.state.users} />
                <div className="form-container">
                    <div className="table-container">
                        {localStorage.accessLevel >= ACCESS_LEVEL_ADMIN ? (
                            <div className="add-new-products">
                                <Link className="blue-button" to={"/AddProduct"}>
                                    Add New Product
                                </Link>
                            </div>
                        ) : null}
                    </div>
                </div>
            </div>
        )
    }
}
