import React, {Component} from "react"
import {Link} from "react-router-dom"
import {ACCESS_LEVEL_GUEST, ACCESS_LEVEL_NORMAL_USER, SERVER_HOST} from "../config/global_constants";
import axios from "axios";
import Logout from "./Logout";
import ProductTableRow from "./ProductTableRow";


export default class UserProfiles extends Component
{

    componentDidMount() {

        this.props.product.photos.map(photo =>
        {
            return axios.get(`${SERVER_HOST}/products/photo/${photo.filename}`)
                .then(res =>
                {
                    if(res.data)
                    {
                        if (res.data.errorMessage)
                        {
                            console.log(res.data.errorMessage)
                        }
                        else
                        {
                            document.getElementById(photo._id).src = `data:;base64,${res.data.image}`
                        }
                    }
                    else
                    {
                        console.log("Record not found")
                    }
                })
        })
    }

    render()
    {
        return (
            <div className="product-container">
                <div className="products">
                {/*    {localStorage.accessLevel > ACCESS_LEVEL_GUEST ? (*/}
                {/*        <>*/}
                {/*            <li className="logout"><Logout /></li>*/}
                {/*            {*/}
                {/*                localStorage.profilePhoto !== "null"*/}
                {/*                    ? <img id="profilePic" src={`data:;base64,${localStorage.profilePic}`} alt=""/>*/}
                {/*                    : null*/}
                {/*            }*/}
                {/*        </>*/}

                {/*    ) : (*/}
                {/*        <>*/}
                {/*            <li className="signIn"><Link className="green-button" to={"/Login"}>Sign In</Link></li>*/}
                {/*            <li className="register"><Link className="blue-button" to={"/Register"}>Register</Link></li>*/}
                {/*        </>*/}
                {/*    )}*/}
                    <div className="product-info">
                        <div className="info">{this.props.user.name}</div>
                        <div className="info">{this.props.user.email}</div>
                        <div className="product-actions">
                            {localStorage.accessLevel > ACCESS_LEVEL_NORMAL_USER ? <Link className="red-button" to={"/DeleteProduct/" + this.props.product._id}>Delete</Link> : null}
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
