import React, {Component} from "react"
import {Link} from "react-router-dom"

import axios from "axios"

import ProductTable from "./ProductTable"
import Logout from "./Logout"
import {ACCESS_LEVEL_GUEST, ACCESS_LEVEL_ADMIN, SERVER_HOST} from "../config/global_constants"


export default class DisplayAllProduct extends Component
{
    constructor(props) 
    {
        super(props)

        this.state = {
            products:[],
            isChecked : {
                blue: false
            }
        }
    }

    componentDidMount() 
    {
        axios.get(`${SERVER_HOST}/products`)
        .then(res => 
        {
            if(res.data)
            {
                if (res.data.errorMessage)
                {
                    console.log(res.data.errorMessage)    
                }
                else
                {           
                    console.log("Product read")
                    this.setState({products: res.data})
                }   
            }
            else
            {
                console.log("Product not found")
            }
        })
    }

    handleCheckboxChange = (attribute) => {
        this.setState((prevState) => ({
            isChecked: {...prevState.isChecked, [attribute]: !prevState.isChecked[attribute],
            }
        }))
    }


    render() 
    {   
        return (           
            <div className="form-container">
                {localStorage.accessLevel > ACCESS_LEVEL_GUEST ?
                    <div className="logout">
                    <Logout/>
                </div>
                :
                <div>
                    <Link className="green-button" to={"/Login"}>Login</Link>
                    <Link className="blue-button" to={"/Register"}>Register</Link>
                    <Link className="red-button" to={"/ShoppingCart"}>Shopping Cart</Link>
                </div>
                }
                <div className="table-container">
                    <input
                        id={"blue"}
                        type="checkbox"
                        checked={this.state.blueIsChecked}
                        onChange={() => this.handleCheckboxChange("blue")}
                    />
                    <ProductTable products={this.state.products} />
                    {localStorage.accessLevel >= ACCESS_LEVEL_ADMIN ?
                    <div className="add-new-products">
                        <Link className="blue-button" to={"/AddProduct"}>Add New Product</Link>
                    </div>
                        :
                        null
                    }
                </div>
            </div> 
        )
    }
}
